app.directive("toggleSwitch",function(){return{restrict:"E",scope:{model:"=model",option:"=option"},template:"<div ng-click='toggleValue()' ng-class='{active:value()}' class='radio-button'><span class='toggle'></span></div>",controller:function(o,e){o.model||(o.model=""),o.toggleValue=function(){o.model[o.option]=o.model[o.option]===!1?!0:!1},o.value=function(){return o.model[o.option]===!0}}}});